import React, { useState, useEffect, useContext} from 'react';

//Import icons 

import { RiWallet3Line, RiArrowDownSLine,
RiArrowUpSLine } from 'react-icons/ri';

// import headless ui
 import { Menu } from '@headlessui/react';

 //import house context
import { HouseContext } from './HouseContext';

const PriceRangeDropdown = () => {
  const {price, setPrice} =
   useContext(HouseContext);

   const [isOpen, setIsOpen] = useState(false);

   const prices = [
    {
      value: 'Price range (any)',
    },
    {
      value: 'R$ 100.000,00 - R$ 150.000,00',
    },
    {
      value: 'R$ 150.000,00 - R$ 200.000,00',
    },
    {
      value: 'R$ 200.000,00 - R$ 400.000,00',
    },
    {
      value: 'R$ 400.000,00 - R$ 500.000,00',
    },
    {
      value: 'R$ 500.000,00 - R$ 1.000.000,00',
    },
   ]

  return (
    <Menu as='div' className='dropdown relative'>
       <Menu.Button onClick={()=> setIsOpen(!isOpen)}
        className='dropdown-btn w-full text-left'>
      <RiWallet3Line
      className='dropdown-icon-primary'/>
      <div>
        <div className='text-[15px] font-medium
        leading-tight' >{price}</div>
        <div className='text-[13px]'>Choose the price range</div>

      </div>
      {
          isOpen ? (
            <RiArrowUpSLine className='dropdown-icon-secondary' />
          ) : ( 
            <RiArrowDownSLine className='dropdown-icon-secondary' />
          )
        }
    </Menu.Button>

    <Menu.Items className='dropdown-menu'>
      {price.map((price, index)=> {
        return (
          <Menu.Item
          onClick={()=> setPrice(price.value)}
          className='cursor-pointer
          hover:text-violet-700 transition'
          as='li' key={index}>
            {price.value}
          </Menu.Item>
        )
      })}
    </Menu.Items>
    </Menu>
  );
};

export default PriceRangeDropdown;
